		               {						die('Erreur : '.$e->getMessage());					                }							$id_cinema= $_POST['id_cinema'];						$req = $bdd->prepare('select *  from films where films.id_cinema = :id_cinema');						$req->execute(array(								'id_cinema' => $id_cinema,								));						while($donnee = $req->fetch())						{				?>						<tr>						<td><?php echo $donnee['titre'];?></td>						<td><?php echo $donnee['duree'];?></td></tr>						<?php						}						$req->closeCursor();		?>												</table>	</center>				</p>			</form>			</head><body><CENTER><table width="2000" height="77" border="0">  <tr>    <td width="666"> <span class="choix">CHOISISSEZ VOTRE SALLE</span> <a href="choix_salle_pour_film.php?id_cinema=<?php echo $id_cinema;?>"> <img src="gauche.png" width="120" height="81" /></a></td>	    <td width="358"><span class="choix">CHOISISSEZ L'HORAIRE DU FILM</span> <a href="choix_horaire.php?id_cinema=<?php echo $id_cinema;?>"><img src="droite.png" width="120" height="81" /></a></td>  </tr></table></CENTER>													        </div>			</div>		        </body></html>